var point = require('../');
var sArgv = process.argv.slice(2).join(' ');
var pts = point(sArgv);

var len = pts.length();

var sPoints = "";
var aPoint;
for (var i = 0; i <= 20; i++) {
    aPoint = pts.at(i / 20 * len);
    sPoints += String(aPoint[0]) + "," + String(aPoint[1]) + " ";
    console.log(i / 20, aPoint);
}
console.log(sPoints);

